import { isArray } from 'lodash';

import React from 'react';
import { Image } from 'antd';
import { StencilProps } from './StencilEditor';

const wrapCss: React.CSSProperties = {
  width: '100%',
  height: '100%',
  overflow: 'hidden',
  borderRight: '4px solid #2dcfcf',
  boxShadow: '0 0 4px 0 rgba(0, 0, 0, 0.4)',
};

const contentCss: React.CSSProperties = {
  backgroundColor: 'white',
  height: '100%',
  borderRadius: '0 8px 8px 0',
  overflow: 'hidden',
};
const fallback =
  'data:image/png;base64,';
export const Card = React.memo<StencilProps>(
  ({ data = {}, text, setEditing, title, description, img, onSave, style }: any): JSX.Element => {
    //console.log(title, ':', img);
    if (isArray(img)) {
      if (img.length > 0) img = img[0];
      else img = undefined;
    }
    return (
      <div style={{ ...wrapCss, ...style }}>
        <div style={contentCss}>
          <div style={{ height: 60, float: 'left', paddingRight: '3px' }}>
            {img ? <Image style={{ height: 60, maxWidth: 60, width: 'auto' }} src={img} fallback={fallback} /> : null}
          </div>
          <div
            style={{
              maxHeight: '2.4em',
              lineHeight: 1.1,
              padding: '4px 4px 6px 0',
              textAlign: 'left',
              fontFamily: 'Lato,Tahoma,sans-serif',
              fontSize: '14px',
              fontWeight: 500,
              overflow: 'hidden',
              textOverflow: 'ellipsis',
            }}>
            {title}
          </div>
          <div
            style={{
              margin: '4px',
              lineHeight: 1,
              fontFamily: 'Lato,Tahoma,sans-serif',
              fontSize: '12px',
              color: '#A9A9A9',
            }}>
            {description}
          </div>
        </div>
      </div>
    );
  },
  (prev: any, next: any) => {
    if (prev.data?.editing !== next.data?.editing) {
      return false;
    }
    return true;
  },
);
